produits_db: Dict[int, Produit] = {p.id: p for p in catalogue}

# Helpers

def get_produit(p_id: int):
    return produits_db.get(int(p_id))


def total_from_ids(ids_list: List[int]) -> int:
    return sum(produits_db[int(pid)].prix for pid in ids_list if int(pid) in produits_db)
